 public void UpdateRoleTest()
{
    var target = ServiceHelper.Create<ISecurityModifier>();
    //將SupervisorMemberID = "000062"=>"000110"
    //則relation : 刪除 000062 , 新增 000110
    Role role = new Role { ID = "AAA3", Name = "AAA3", SupervisorMemberID = "000110", AllowMemberTypes = new MemberType[] { MemberType.Member }.ToList() };
    target.UpdateRole(role);//測OK
    //將SupervisorMemberID = "XXXX"=>"" (清空)
    //則 relation 將 : 判斷XXXX，再決定刪除之
    role = new Role { ID = "AAA3", Name = "AAA3", SupervisorMemberID = "", AllowMemberTypes = new MemberType[] { MemberType.Member }.ToList() };
    target.UpdateRole(role);//測OK
}

 public void InsertPermissions()
{
    /* 1.測權限新增/更新
     * 2.測管理員新增/刪除
     */
    var datas = new List<Permission>();
    var p1 = new Permission { GUID = Guid.Parse("155d3a37-3ae6-4cf7-984b-2d70a5cb5da6"), ID = "TTT" };
    datas.Add(p1);
    var p2 = new Permission { ParentGUID = Guid.Parse("155d3a37-3ae6-4cf7-984b-2d70a5cb5da6"), ID = "TTT.Car", GUID = Guid.NewGuid() };
    datas.Add(p2);
    p2.Managers = new PermissionManager[] { 
        new PermissionManager { ManagerId = "900072", PermissionGuid = p2.GUID, UnitGuid = Guid.Parse("155d3a37-3ae6-4cf7-984b-2d70a5cb5da6") },
        new PermissionManager { ManagerId = "900082", PermissionGuid = p2.GUID, UnitGuid = Guid.Parse("155d3a37-3ae6-4cf7-984b-2d70a5cb5da6") }
    };
    var service = ServiceHelper.Create<ISecurityModifier>();
    service.InsertPermissions(datas);//測新增OK
    p2.Managers = new PermissionManager[] { };
    service.UpdatePermission(p2); //測刪OK
}

 public void DeletePermissions()
{
    var datas = new List<Permission>();
    datas.Add(new Permission { GUID = Guid.Parse("c283e136-6150-4b38-b10b-5cfb765f6d11") });
    var service = ServiceHelper.Create<ISecurityModifier>();
    service.DeletePermissions(datas);    //OK        
}

 public void InsertPermissions()
{
    /* 1.測權限新增/更新
