    datas.Add(p1);
    var p2 = new Permission { ParentGUID = Guid.Parse("155d3a37-3ae6-4cf7-984b-2d70a5cb5da6"), ID = "TTT.Car", GUID = Guid.NewGuid() };
    datas.Add(p2);
    p2.Managers = new PermissionManager[] { 
        new PermissionManager { ManagerId = "900072", PermissionGuid = p2.GUID, UnitGuid = Guid.Parse("155d3a37-3ae6-4cf7-984b-2d70a5cb5da6") },
        new PermissionManager { ManagerId = "900082", PermissionGuid = p2.GUID, UnitGuid = Guid.Parse("155d3a37-3ae6-4cf7-984b-2d70a5cb5da6") }
    };
    var service = ServiceHelper.Create<ISecurityModifier>();
