 public void GetParentPermissionsTest()
{
    var target = ServiceHelper.Create<ISecurityProvider>();
    IEnumerable<Permission> permissions = target.GetPermissions(new Guid[] { Guid.Parse("07c33594-a8b8-49c7-8cab-a9a8c9cc32ab"), Guid.Parse("2247582d-0aec-4056-83d3-58a664a949fd"), Guid.Parse("82579ba9-92b1-4b5d-a1df-97e230a3c95e") });
    IEnumerable<Permission> parents = Enumerable.Empty<Permission>();
    parents = target.GetParentPermissions(permissions);
    var result = parents.ToList();
    Assert.AreEqual(parents.Count(), 5);
    //再取得其完整的描述文字-測試
    var allPermission = parents.Concat(permissions);
    var result2 = target.GetPermissionFullDescriptions(allPermission);
    var list2 = result2.ToList();
}


 public void GetParentPermissionsTest()
{
    var target = ServiceHelper.Create<ISecurityProvider>();
    IEnumerable<Permission> permissions = target.GetPermissions(new Guid[] { Guid.Parse("07c33594-a8b8-49c7-8cab-a9a8c9cc32ab"), Guid.Parse("2247582d-0aec-4056-83d3-58a664a949fd"), Guid.Parse("82579ba9-92b1-4b5d-a1df-97e230a3c95e") });
    IEnumerable<Permission> parents = Enumerable.Empty<Permission>();
    parents = target.GetParentPermissions(permissions);
    var result = parents.ToList();
    Assert.AreEqual(parents.Count(), 5);
    //再取得其完整的描述文字-測試
    var allPermission = parents.Concat(permissions);
    var result2 = target.GetPermissionFullDescriptions(allPermission);
    var list2 = result2.ToList();
}

