}

  public void SetAccountProfilesTest()
{
    var target = ServiceHelper.Create<ISecurityService>();
    IEnumerable<sc_account_profile> profiles = new sc_account_profile[]{
        new sc_account_profile{ iaccount="900072", ikey="myKey",fvalue="myValue"}};
    target.SetAccountProfiles(profiles);
}

