 public void CreateTest()
{
    var service = ServiceHelper.Create<IMenuService>();
    service.Create(new Menu { GUID = Guid.NewGuid(), Name = "測試", Url = "TTT/TTT", VerifyType = MenuVerifyType.ByExpression, AllowPermissionExpression = "Mis.*" });
}

