 public void ProgramInsert()
{
    var service = ServiceHelper.Create<IProgramService>();
    var model = new Program { ID = "AAA", Name = "AAA", AllowPermissionExpression = "AA", HelpText = "AA測試", IsAllowInternet = false, dcreate = DateTime.Now, dupdate = DateTime.Now };
    model.Profile = new sc_program_profile[] { 
        new sc_program_profile{ iprogram = model.ID , ikey="QQ",fvalue="QQ"}, 
        new sc_program_profile{ iprogram = model.ID , ikey="QQ3",fvalue="QQ3_up"}, 
        new sc_program_profile{ iprogram = model.ID , ikey="QQ4",fvalue="QQ4"},
    }.ToList();
    //新增
    service.Create(model);//測OK
    //更新
    service.Update(model);
    //刪除
    service.Delete(model);//測OK
}

